/**
 * Clase encargada de definir la información de los Pokemons.
 * @param nombre Nombre del Pokemon.
 * @param peso Peso del Pokemon.
 * @param altura Altura del Pokemon.
 * @param tipo Tipo del Pokemon, comprendido entre Hierba, Fuego, Agua o Electricidad.
 * @param Estadisticas Estadísticas bases de los pokemons definidos, estas son ataque, defensa, velocidad y Vida.
 */

export class Pokemon {

  private Estadisticas = {
    ataque: 0,
    defensa: 0,
    velocidad: 0,
    salud: 0,
  }
  constructor(private nombre: string, private peso: number, private altura: number, private tipo: string, Estadisticas: [number, number, number, number]) {

    this.nombre = nombre;
    this.peso = peso;
    this.altura = altura;
  	this.tipo = tipo;

    this.Estadisticas.ataque = Estadisticas[0];
    this.Estadisticas.defensa = Estadisticas[1];
    this.Estadisticas.velocidad = Estadisticas[2];
		this.Estadisticas.salud = Estadisticas[3];
  }

  /**
  * Funcion 'getNombre' que devuelve el nombre del pokemon.
  * @returns Devuelve el nombre del Pokemon.
  */
  public getNombre() {
    return this.nombre;
  }

  /**
  * Funcion 'getPeso' que devuelve el peso del pokemon.
  * @returns Devuelve el peso del Pokemon.
  */

  public getPeso() {
    return this.peso;
  }

  /**
  * Funcion 'getAltura' que devuelve la altura del pokemon.
  * @returns Devuelve la altura del Pokemon.
  */

  public getAltura() {
    return this.altura;
  }


  /**
  * Funcion 'getTipo' que devuelve el tipo del pokemon.
  * @returns Devuelve el tipo del Pokemon.
  */
  public getTipo() {
    return this.tipo;
  }

  /**
  * Funcion 'getEstadistica' que devuelve las estadísticas del pokemon.
  * @returns Devuelve la estadistica seleccionada del Pokemon.
  */
  public getEstadisticas() {
    return this.Estadisticas;
  }


	/**
  * Funcion 'getNombre' que devuelve el nombre del pokemon.
  * @param puntosVida Nuevo valor a actualizar en la vida del pokemon
  * @returns Devuelve la vida actualizada del Pokemon.
  */
  public setSalud(puntosVida: number) {
    this.Estadisticas.salud = puntosVida;
  }
}

import {Pokemon} from './pokemon';

/**
 * Clase encargada de definir el funcionamiento de la Pokedex a través de un vector de Pokemons.
 * @param baseDatosPokemon Es un vector de Pokemons (Pokedex), que recoge a cada Pokemon y lo almacena en el vector.
 */
export class Pokedex {

  constructor(private baseDatosPokemon: Pokemon[]){
    this.baseDatosPokemon = baseDatosPokemon;
  }

  /**
   * Función que devuelve todos los Pokemons de la Base de Datos.
   * @returns Devuelve el vector con todos los pokemons registrados.
   */
  public getPokedex() {
    return this.baseDatosPokemon;
  }

  /**
   * Función que recibe un Pokemon y lo guarda en la Pokedex.
   * @param Pokemon Pokemon que entra a la base de datos de la Pokedex.
   */
  public setPokedex(Pokemon: Pokemon) {
    this.baseDatosPokemon.push(Pokemon);
  }

  /**
   * Función que recibe un Pokemon y compara con la Base de datos si existe ese Pokemon.
   * @param Pokemon Nombre del pokemon que se desea Buscar dentro de la Pokedex.
   * @return Devuelve en caso de encontrar al pokemon buscado en la Base de datos sino devuelve undefined
   */
  public buscarPokemon(Pokemon: Pokemon): Pokemon | undefined {
    let auxiliar: number = -1;

    this.baseDatosPokemon.forEach((i) => {
      if (i == Pokemon) {
        auxiliar = this.baseDatosPokemon.indexOf(i);
      }
    });
    if (auxiliar == -1) {
      return undefined;
    }

    return this.baseDatosPokemon[auxiliar];
  }

  /**
   * Función que muestra la base de datos de la Pokedex
   */
  public mostrarPokedex() {
    console.log(`──────────────────────────────────────────────`);
    console.log(`	» Los Pokemons Recogidos en la Pokedex son:«`);
    console.log(this.baseDatosPokemon);
    console.log(`──────────────────────────────────────────────`);
  }

}

import { Pokemon } from "./pokemon";
/**
 * Clase encargada de simular el combate entre dos Pokemons.
 * @param pokemon1 Primer pokemon que se enfrenta en el combate.
 * @param pokemon2 Contrincante del Pokemon 1.
 */

export class Combat {

  constructor(private pokemon1: Pokemon, private pokemon2: Pokemon){
    this.pokemon1 = pokemon1;
    this.pokemon2 = pokemon2;
  }
  /**
   * Función que calcula el daño inflingido de un pokemon a otro y viceversa.
   * @param atacante Iniciador de la pelea entre ambos pokemons.
   * @return Devuelve el daño inflingido según la fórmula especificada que depende de la efectividad entre los tipos elementales de ambos pokemons.
   */

  public calculoDañoInflingido(atacante: number): number {
    let efectividad: number = 0;
    let daño: number = 0;

    if (this.pokemon1.getTipo() == this.pokemon2.getTipo()) {
      efectividad = 0.5;
    } else {

      if (this.pokemon1.getTipo() == "fuego") {
        if (this.pokemon2.getTipo() == "hierba") {
          efectividad = 2;
        }
        if (this.pokemon2.getTipo() == "agua") {
          efectividad = 0.5;
        }
        if (this.pokemon2.getTipo() == "electrico") {
          efectividad = 1;
        }
      }

      if (this.pokemon1.getTipo() == "hierba") {
        if (this.pokemon2.getTipo()== "fuego") {
          efectividad = 0.5;
        }
        if (this.pokemon2.getTipo() == "agua") {
          efectividad = 2;
        }
        if (this.pokemon2.getTipo() == "electrico") {
          efectividad = 1;
        }
      }

      if (this.pokemon1.getTipo() == "agua") {
        if (this.pokemon2.getTipo() == "electrico") {
          efectividad = 0.5;
        }
        if (this.pokemon2.getTipo() == "fuego") {
          efectividad = 2;
        }
        if (this.pokemon2.getTipo() == "hierba") {
          efectividad = 0.5;
        }
      }

      if (this.pokemon1.getTipo() == "electrico") {
        if (this.pokemon2.getTipo() == "fuego") {
          efectividad = 1;
        }
        if (this.pokemon2.getTipo() == "agua") {
          efectividad = 2;
        }
        if (this.pokemon2.getTipo() == "hierba") {
          efectividad = 1;
        }
      }
    }

    if (atacante == 1) {
      daño = (50 * (this.pokemon1.getEstadisticas().ataque / this.pokemon2.getEstadisticas().defensa) * efectividad);
      return Math.trunc(daño);
    } else {

      if (efectividad == 0.5) {
        efectividad = 2;
      }
      if (efectividad == 2) {
        efectividad = 0.5;
      }
      daño = (50 * (this.pokemon2.getEstadisticas().ataque / this.pokemon1.getEstadisticas().defensa) * efectividad);
      return Math.trunc(daño);
    }  
  }

  /**
   * Función que muestra las diferentes estadísticas de los Pokemons y calcula el Ganador del combate pokemon.
   * @return Devuelve una cadena con el nombre del Pokemon que ha ganado dependiendo de la vida resultante.
   */
  public combatePokemon(): string {
    let atacante: number = 1;
    if( this.pokemon1.getEstadisticas().velocidad >= this.pokemon2.getEstadisticas().velocidad) {
      atacante =  1;
    } else {
      if( this.pokemon1.getEstadisticas().velocidad < this.pokemon2.getEstadisticas().velocidad) {
        atacante =  2;
      }
    }

    console.log(`──────────────────────────────────────────────────────────────────`);
    console.log(`» ${this.pokemon1.getNombre()}  vs   ${this.pokemon2.getNombre()}`);
    console.log(`» Vida: ${this.pokemon1.getEstadisticas().salud}      » Vida: ${this.pokemon2.getEstadisticas().salud}`);
    console.log(`» Ataque: ${this.pokemon1.getEstadisticas().ataque}    » Ataque: ${this.pokemon2.getEstadisticas().ataque}`);
    console.log(`» Defensa: ${this.pokemon1.getEstadisticas().defensa}   » Defensa: ${this.pokemon2.getEstadisticas().defensa}`);
    console.log(`» Velocidad: ${this.pokemon1.getEstadisticas().velocidad} » Velocidad: ${this.pokemon2.getEstadisticas().velocidad}`);
    console.log(`──────────────────────────────────────────────────────────────────`);

    while( (this.pokemon1.getEstadisticas().salud > 0) && (this.pokemon2.getEstadisticas().salud > 0) ) {
      if (atacante == 1) {
        console.log(`»» Turno de ataque de ${this.pokemon1.getNombre()}`);
        this.pokemon2.setSalud(this.pokemon2.getEstadisticas().salud - this.calculoDañoInflingido(atacante));
        console.log(`»» La vida de ${this.pokemon2.getNombre()} ha bajado a: ${this.pokemon2.getEstadisticas().salud}\n`);
        atacante = 2;
      } else {
        console.log(`»» Turno de ataque de ${this.pokemon2.getNombre()}`);
        this.pokemon1.setSalud(this.pokemon1.getEstadisticas().salud - this.calculoDañoInflingido(atacante));
        console.log(`»» La vida de ${this.pokemon1.getNombre()} ha bajado a: ${this.pokemon1.getEstadisticas().salud}\n`);
        atacante = 1;
      }
    }

    if(this.pokemon1.getEstadisticas().salud <= 0) {
      console.log(`»»»» El ganador del combate es: ${this.pokemon2.getNombre()} «««« `);
      return this.pokemon2.getNombre();
    } else {
      console.log(`»»»» El ganador del combate es: ${this.pokemon1.getNombre()} ««««`);
      return this.pokemon1.getNombre();
    }

  }

}

/**
 * Clase que define la creación y el comportamiento de un Tablero de juego del Conect-4
 * @param fil Numero de Filas del Tablero
 * @param col Numero de Columnas del Tablero
 * @param value Valor que toma una posición en el Tablero
 * @param tablero Un vector de vectores de tipo numerico que define el tablero 
 */
export class Tablero {
  private fil: number;
  private col: number;
  private value: string;
  private tablero : Array<Array<string>> =  new Array<Array<string>>();

  constructor(fil: number, col: number, value:string) {
    this.fil = fil;
    this.col = col;
    this.value = value;
    this.initFilas(fil);
    this.initColumnas(col, value);

  }

  /**
   * Funcion que devuelve las filas del tablero
   * @returns las filas del tablero
   */
  getFil(){
    return this.fil;
  }

  /**
   * Funcion que devuelve las columnas del tablero
   * @returns las columnas del tablero
   */
  getCol() {
    return this.col;
  }
  /**
   * Método privado que inicializa las filas de un tablero
   * @param filas El numero de filas que se le pasa.
   * @returns dependiendo de si el numero es válido devuelve en caso de serlo un number o undefined en caso de no serlo.
   */
  private initFilas(filas: number): void | undefined {
    if (filas < 1) {
      return undefined;
    } else {
      for(let i: number = 0; i < filas; i++) {
        this.tablero.push(new Array<string>());
      }
    }
  }

  /**
   * Función privada que inicializa las columnas del tablero e indexa un valor en esa posición.
   * @param columnas numero de columnas para esas posiciones.
   * @param valor Valor que ira en las posiciones, inicialmente a 0.
   */

  private initColumnas(columnas: number, valor: string): void | undefined {
    if (columnas < 1) {
      return undefined;
    }
    for (let i: number = 0; i < this.tablero.length; i++ ) {
      for(let j: number = 0; j < columnas; j++) {
        this.tablero[i].push(valor);
      }
    }
  }

  /**
   * Función pública que devuelve el tablero creado 
   * @returns devuelve el objeto tablero creado en la clase.
   */
  public getTablero() {
    return this.tablero;
  }

  /** 
   * Función que devuelve el valor de una posicion concreta.
   * @param filas posicion de filas en las que se quiere encontrar el valor.
   * @param columnas posicion de columnas en la que se quiere encontrar el valor.
   * @returns devuelve el valor en esa posicion si es una posicion definida en la matriz, sino devuelve un undefined.
   */

  public getValue(filas: number, columnas: number): string | undefined {
    if(filas < 0 || filas >= this.fil || columnas < 0 || columnas >= this.col) {
      return undefined;
    }
    return this.tablero[filas][columnas];

  }

  /**
   * Funcion que imprime por pantalla el tablero.
   */
  public imprimirTablero() {
    console.log(`Visualización del Tablero:`);
    console.log(this.tablero);
  }

  /**
   * Funcion publica que analiza si una columna esta llena
   * @param columna columna donde se quiere colocar una ficha
   * @returns devuelve la fila si esta vacía
   */
  public obtenerFilaLibre(columna: number): number {
    for (let i:number = this.fil -1; i >= 0 ; i--) {
      if(this.tablero[i][columna] == '') {
        return i;
      }
    }
    return -1;
  }

  /**
   * Función publicaque coloca una ficha en la columna especificada
   * @param valor valor O u X que indica las fichas
   * @param columna columna donde se coloca la ficha
   * @returns devuelve la ficha colocada en ese tablero
   */
  public colocarFicha(valor: string, columna:number): void | undefined {
    if( columna < 0 || columna >= this.col) {
      return undefined;
    }

    let filas: number = this.obtenerFilaLibre(columna);
    this.tablero[filas][columna] = valor;

  } 

  /**
   * Funcion encargada de limpiar el tablero
   * @returns devuelve el tablero vacio.
   */
  public limpiartableroVacio() {
    for(let i: number = 0; i < this.col; i ++) {
      let resultado: number = this.obtenerFilaLibre(i); 
      if (resultado != -1) {
        return 0;
      }
    }
    return 1;
  }
  // Norte (i-1,j), Sur(i+1,j), este(i,j+1), oeste(i,j-1)
  /**
   * Función que cuenta el numero de fichas colocadas en las posiciones Norte.
   * @returns numero de fichas de un jugador colocadas en vertical.
   */
  public contarNorte() {
    let contador: number = 0;
    for(let i: number = this.fil-1; i >= 0 ; i++) {
      for(let j:number = 0; j < this.col; j++) {
        if((this.tablero[i-1][j] == 'x') || (this.tablero[i-1][j] == 'O')) {
          contador ++;
        }
      }
    }

    return contador;
  }

  /**
   * Función que cuenta el numero de fichas colocadas en la posicion Este.
   * @returns numero de fichas colocadas en horizontal.
   */
  public contarEste() {
    let contador: number = 0;
    for(let i: number = this.fil-1; i >= 0 ; i++) {
      for(let j:number = 0; j < this.col; j++) {
        if((this.tablero[i][j]+1 == 'x') || (this.tablero[i][j+1] == 'O')) {
          contador ++;
        }
      }
    }

    return contador;
  }

  /**
   * Funcion que cuenta el numero de fichas colcoadas en NortEste.
   * @returns numero de fichas colocadas en diagonal.
   */
  public contarDiagonal() {
    let contador: number = 0;
    for(let i: number = this.fil-1; i >= 0 ; i++) {
      for(let j:number = 0; j < this.col; j++) {
        if((this.tablero[i-1][j+1] == 'x') || (this.tablero[i-1][j+1] == 'O')) {
          contador ++;
        }
      }
    }
    return contador;
  }

}

